/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { ContentEditable } from 'components/ContentEditable';
import Button from 'components/UI/Button/Button';
import { Input } from 'components/UI/Input';
import React, { FC, ReactNode, useState } from 'react';
import { theme } from 'utils/theme';

interface IAddPageProps {

};

const styles = {
  root: {
    base: css({
      display: 'flex',
      flexDirection: 'column',
      gap: '10px',
      '& > *': {

      }
    })
  },
  buttonGroup: {
    base: css({
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      justifyContent: 'center',
    })
  },
  button: {
    clean: css({
      // background: theme.palette.primary.normal
      flexGrow: 1,
    }),
    submit: css({
      background: theme.palette.primary.lightest,
      flexGrow: 1,
    })
  }
}

const AddPage: FC<IAddPageProps> = () => {
  const [word, setWord] = useState<any>('');
  const [node, setNode] = useState<ReactNode>();

  return (
    <div css={styles.root.base}>
      <Input.TextArea
        sizing='large'   
        placeholder='Word' 
      >
        {node}
      </Input.TextArea>
      <Input 
        sizing='large'   
        placeholder='Meaning' 
        // onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <Input 
        sizing='large'   
        placeholder='Example' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <Input 
        sizing='large'   
        placeholder='Image' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <div css={styles.buttonGroup.base}>
        <Button css={styles.button.clean} outline>Clean</Button>
        <Button css={styles.button.submit}>Submit</Button>
      </div>
    </div>
  )
};

export default AddPage;
