/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import Button from 'components/UI/Button/Button';
import { Input } from 'components/UI/Input';
import React, { FC, useState } from 'react';
import { theme } from 'utils/theme';

interface IAddPageProps {

};

const styles = {
  root: {
    base: css({
      display: 'flex',
      flexDirection: 'column',
      gap: '10px',
      '& > *': {

      }
    })
  },
  buttonGoupe: {
    base: css({
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      justifyContent: 'flex-end',
    })
  },
  button: {
    clean: css({
      // background: theme.palette.primary.normal
    }),
    submit: css({
      background: theme.palette.primary.lightest
    })
  }
}

const AddPage: FC<IAddPageProps> = () => {
  const [word, setWord] = useState<string>('');

  return (
    <div css={styles.root.base}>
      <Input 
        sizing='large'   
        placeholder='Word' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <Input 
        sizing='large'   
        placeholder='Meaning' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <Input 
        sizing='large'   
        placeholder='Example' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <Input 
        sizing='large'   
        placeholder='Image' 
        onChange={(e) => setWord(e.currentTarget.value)}
        value={word}
      />
      <div css={styles.buttonGoupe.base}>
        <Button css={styles.button.clean} outline>Clean</Button>
        <Button css={styles.button.submit}>Submit</Button>
      </div>
    </div>
  )
};

export default AddPage;
