import { SelectedDefinitionType } from "models/AnkiField";
import { atom, selector } from "recoil";

export const SELECTED_DEFINITION = 'selectedDefinition';

const selectedDefinitionState = atom<SelectedDefinitionType>({
  key: `${SELECTED_DEFINITION}State`,
  default: {
    word: undefined,
    definition: undefined,
    example: undefined,
  },
})

export const selectedDefinitionSelector = selector<SelectedDefinitionType>({
  key: SELECTED_DEFINITION,
  get: ({get}) => get(selectedDefinitionState),
  set: ({set}, newValue) => {
    if (chrome.storage) {
      chrome.storage.sync.set({[SELECTED_DEFINITION]: newValue})
    }
    return set(selectedDefinitionState, newValue);

  }
})
