import { atom, selector } from "recoil";

export type CurrentTabType = 'search' | 'add' | 'settings' | 'about';

export const currentTabState = atom<CurrentTabType>({
  key: 'currentTab',
  default: 'search',
})

export const currentTabSelector = selector<CurrentTabType>({
  key: 'currentTab',
  get: ({get}) => get(currentTabState),
  set: ({}, set) => {
    if (!chrome.storage) {
      return set
    }
    const dataFromStorage = chrome.storage
  }
})
