import React, { FC, ReactNode } from 'react';
import { Type } from 'typescript';

type IContentEditableProps<T extends React.ElementType> = {
  component?: T;
  onContentChange?: (event: ReactNode[]) => void;
} & React.ComponentPropsWithoutRef<T>;

const ContentEditable = <T extends React.ElementType = 'div',>({ component, onContentChange, onInput = () => {}, ...other }: IContentEditableProps<T>) => {
  const Component = component || 'div';

  const handleChange = (e: React.FormEvent<HTMLDivElement>) => {
    const imgMaxWidth = e.currentTarget.clientWidth - 30;
    // const firstText;
    const nodes = e.currentTarget.getElementsByTagName('div');
    let parsedNodes: string[] = [];

    for (let i = 0; i < nodes.length; i++) {
      const currentNode = nodes[i];
      const imgs: string[] = [];
      let text: string = undefined;

      if (currentNode.children[0].tagName !== 'BR') {
        const imgElements = currentNode.getElementsByTagName('img');
        if (imgElements) {
          for (let i = 0; i < imgElements.length; i++) {
            const img = imgElements[i];
            if (img.width > imgMaxWidth) img.width = imgMaxWidth;
            imgs.push(img.outerHTML);
          }
        } else if (currentNode.textContent) {
          text = currentNode.textContent;
        }

      }
      parsedNodes = [...imgs];
      text && parsedNodes.push(text);
    }
    console.log(nodes);
  }

  return (
    <Component
      contentEditable
      onInput={(e) => {handleChange(e); onInput(e)}}
      {...other}
    />
  )
};

export default ContentEditable;
