/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { createContext, FC } from 'react';
import { theme } from 'utils/theme';
import { Input } from '../Input';
import Option from './Option';

interface OptionType {
  id: string,
  name: string,
}

interface ISelectProps extends Omit<React.HTMLAttributes<HTMLDivElement>, 'onChange'> {
  options: OptionType[],
  value: string | undefined,
  onChange: (value: string) => void,
};

const styles = {
  root: {
    base: css({

    })
  },
  menu: {
    base: css({
      background: theme.palette.secondary.darker,
      borderRadius: '12px',
      fontSize: '14px',
      marginTop: '10px',
      boxShadow: '0px 0px 4px 0px rgba(34, 60, 80, 0.2)',
      // border: `2px solid ${theme.palette.primary.lightest}`,
    })
  }
}

const Select: FC<ISelectProps> = ({ options, value, ...other }) => {

  return (
    <div
      css={styles.root.base}
      {...other}
    >
      <Input placeholder='Card tipe' />
      <div
        css={styles.menu.base}
      >
        {options.map((option) => (
          <Option id={option.id} isChecked={value ? value === option.id : false}>{option.name}</Option>
        ))}
      </div>
    </div>
  )
};

export default Select;
