import React, { FC, ReactNode } from 'react';
import { Type } from 'typescript';

type IContentEditableProps<T extends React.ElementType> = {
  component?: T;
  onContentChange?: (event: ReactNode[]) => void;
} & React.ComponentPropsWithoutRef<T>;

const ContentEditable = <T extends React.ElementType = 'div',>({ component, onContentChange, onInput = () => {}, ...other }: IContentEditableProps<T>) => {
  const Component = component || 'div';

  const handleChange = (e: React.FormEvent<HTMLDivElement>) => {
    const innerHTML = e.currentTarget.innerHTML;
    const allImgs = e.currentTarget.getElementsByTagName('img');
    for (let i = 0; i < allImgs.length; i++) {
      allImgs[i].width = 100;
    }
    console.log(allImgs);
    console.log(innerHTML);
  }

  return (
    <Component
      contentEditable
      onInput={(e) => {handleChange(e); onInput(e)}}
      {...other}
    />
  )
};

export default ContentEditable;
