export function setCaretPostion() {
  var el = document.getElementById("editable")
  var range = document.createRange()
  var sel = window.getSelection()

  range.setStart(el.childNodes[2], 5)
  range.collapse(true)

  sel.removeAllRanges()
  sel.addRange(range)
}
