/** @jsxImportSource @emotion/react */
import { FC, ReactNode, useEffect, useRef, useState } from 'react';
import { styles } from './Input';
import Label from './Label';
import {Editor, EditorState} from 'draft-js';
import { ContentEditable } from 'components/ContentEditable';


interface ITextAreaProps extends Omit<React.HTMLAttributes<HTMLDivElement>, 'children'>{
  icon?: React.ReactNode,
  withLabel?: boolean,
  css?: {},
  sizing?: 'large' | 'medium',
  multy?: boolean,
  children: ReactNode;
};

const TextArea: FC<ITextAreaProps> = ({ sizing = 'large', icon, placeholder = '', withLabel = true, css = {}, className, children, ...other }) => {
  const [isFocused, setIsFocused] = useState(false);
  const refInput = useRef<HTMLDivElement>(null);

  // useEffect(() => {
  //   if (refInput.current) {
  //     refInput.current.textContent = children;
  //   }
  // })

  return (
    <div
    css={[
      styles.base,
      styles[sizing],
      css,
    ]}
    className={className}
   >
    {icon != null && icon}
    {placeholder !== '' && (
      <Label isLabel={isFocused || children !== '' || children !== undefined} placeholderOnly={!withLabel} >{placeholder}</Label>
    )}
      <ContentEditable
        className='multy-input'
        css={[
          withLabel ? styles.inputWithLabel : styles.inputWithoutLabel,
          styles.placeholder.base,
          sizing === 'medium' && styles.placeholder.medium,
        ]}
        placeholder={!withLabel ? placeholder : undefined}
        onFocus={() => {setIsFocused(true)}}
        onBlur={() => {setIsFocused(false)}}
        {...other}
      />
    </div>
  )
};

export default TextArea;
