/** @jsxImportSource @emotion/react */
import { FC, useState } from 'react';
import { styles } from './Input';
import Label from './Label';
import {Editor, EditorState} from 'draft-js';


interface ITextAreaProps extends React.HTMLAttributes<HTMLDivElement>{
  icon?: React.ReactNode,
  withLabel?: boolean,
  css?: {},
  sizing?: 'large' | 'medium',
  multy?: boolean,
};

const TextArea: FC<ITextAreaProps> = ({ sizing = 'large', icon, placeholder = '', withLabel = true, css = {}, className, children, ...other }) => {
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div
    css={[
      styles.base,
      styles[sizing],
      css,
    ]}
    className={className}
   >
    {icon != null && icon}
    {placeholder !== '' && (
      <Label isLabel={isFocused || children !== ''} placeholderOnly={!withLabel} >{placeholder}</Label>
    )}
      {/* <div
        className='multy-input'
        css={[
          withLabel ? styles.inputWithLabel : styles.inputWithoutLabel,
          styles.placeholder.base,
          sizing === 'medium' && styles.placeholder.medium,
        ]}
        contentEditable
        placeholder={!withLabel ? placeholder : undefined}
        onFocus={() => {setIsFocused(true)}}
        onBlur={() => {setIsFocused(false)}}
        {...other}
      /> */}
      <Editor />
    </div>
  )
};

export default TextArea;
