export function setCaretPosition(elemId, nodeIndex, caretPos) {
  var el = document.getElementById(elemId)
  var range = document.createRange()
  var sel = window.getSelection()

  range.setStart(el.childNodes[nodeIndex], caretPos)
  range.collapse(true)

  sel.removeAllRanges()
  sel.addRange(range)
}
