export function setCaretPosition(elemId, nodeIndex, caretPos) {
  const elem = document.getElementById(elemId);
  const nodes = elem ? elem.getElementsByTagName('div') : undefined;

  console.log(elemId);

  if(nodes !== undefined) {
    for (let i = 0; i < nodes.length; i++) {
      const currentNode = nodes[i];

      if (i === nodeIndex) {
        if(currentNode.createTextRange) {
          var range = elem.createTextRange();
          range.move('character', caretPos);
          range.select();
        }
        else {
          if(currentNode.selectionStart) {
            elem.focus();
            currentNode.setSelectionRange(caretPos, caretPos);
          }
          else
            elem.focus();
        }
        break;
      }

    }
  }
}
