/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { FC, Ref, useEffect, useState } from 'react';
import { useRecoilState } from 'recoil';
import { searchingWordState } from 'store/atoms/searchingWordState';
import { theme } from 'utils/theme';

interface IMainProps extends React.HTMLAttributes<HTMLDivElement> {

};

const styles = {
  base: css({
    background: theme.palette.secondary.normal,
    padding: '110px 25px 40px 25px',
    borderRadius: '0 0 8px 8px',
    // maxHeight: 'calc(100vh - 90px)',
    overflowY: 'auto',
  }),
  setHeight: (height: number) => css({
    maxHeight: height - 90,
  })
}

const Main: FC<IMainProps> = ({ children, ...other }) => {
  const [documentHeight, setDocumentHeight] = useState(600);
  const [searchingWord] = useRecoilState(searchingWordState);

  // const resizeHandler = () => {
  //   setDocumentHeight(window.innerHeight);
  // }

  useEffect(() => {
    if (searchingWord !== '' || searchingWord !== undefined) {
      setTimeout(() => setDocumentHeight(window.innerHeight), 10);
    }
  }, [searchingWord])


  return (
    <main
      css={[
        styles.base,
        styles.setHeight(documentHeight)
      ]}
      {...other}
    >
      {children}
    </main>
  )
};

export default Main;
